mapper.select:
SELECT * FROM ( SELECT rownum rn, a.* FROM (

        SELECT rownum id, board.* 
          FROM board

      ORDER BY board_no

) a WHERE rownum <= #{svo.end} ) b WHERE rn >= #{svo.start}

mapper.insert:

