mapper.select:
SELECT * FROM ( SELECT rownum rn, a.* FROM (

	SELECT rno
	     , bno
	     , reply
	     , replyer
	     , replyDate
	     , updateDate
	FROM tbl_reply
   WHERE bno = #{bno}

) a WHERE rownum <= #{svo.end} ) b WHERE rn >= #{svo.start}

mapper.insert:
INSERT INTO tbl_reply 
	(
    rno
    , bno
    , reply
    , replyer
	)
VALUES 
	(
	 #{rno}
    , #{bno}
    , #{reply}
    , #{replyer}
	)
