mapper.select:
SELECT * FROM ( SELECT rownum rn, a.* FROM (

	SELECT rownum id, e.*
	  FROM employees e

  ORDER BY first_name		

) a WHERE rownum <= #{svo.end} ) b WHERE rn >= #{svo.start}

mapper.insert:

	SELECT employees_seq.nextval 
	  FROM dual

INSERT INTO employees
	(
	  EMPLOYEE_ID
	, FIRST_NAME
	, LAST_NAME
	, EMAIL
	, PHONE_NUMBER phone
	, HIRE_DATE
	, JOB_ID

	, SALARY

	, COMMISSION_PCT
	, MANAGER_ID
	, DEPARTMENT_ID 
	)
VALUES
	(
	 #{employeeId}
	, #{firstName}
	, #{lastName}
	, #{email}
	, #{phoneNumber}
	, #{hireDate}
	, #{jobId}

	, #{salary}

	, #{commissionPct}
	, #{managerId}
	, #{departmentId}
	)
